import { getChapCount } from './count-of-chap';
export function getVerseCount(book1based: number, chap1based: number): number {
    const chaplimit = getChapCount(book1based);
    if (chap1based < 0 || chap1based > chaplimit) {
        throw new Error(`book ${book1based} chap ${chap1based} only have ${chaplimit} verse.`);
    }
    return num[book1based - 1][chap1based - 1];
}

