export class KeywordSearchGetter implements IKeywordSearchGetter {
  async mainAsync(arg: { keyword: string; }): Promise<DOneLine[]> {
    const rr1 = await searchAsync(arg.keyword);
    return rr1.map(a1 => cvt2Line(a1, arg.keyword));

    function cvt2Line(a1: DSeApiRecord, keyword: string) {
      let rre: DText[] = [{ w: a1.bible_text }];
      rre = addKeywords(rre);

      return texts2line(rre);
      function addKeywords(texts: DText[]) {
        const keywords = keyword.split(' ');
        const dict = LQ.from(keywords)
          .select((a1, i1) => ({ na: a1, idx: i1 }))
          .toDictionary(a1 => a1.na, a1 => a1.idx);

        const reTexts: DText[] = [];
        // let reg = new RegExp(keyword, 'gi');
        const reg = new RegExp(keywords.join('|'), 'gi');
        for (const it1 of texts) {
          const reSplit = new SplitStringByRegexVer2().main(it1.w, reg);
          for (const it2 of reSplit) {
            if (it2.exec === undefined) {
              const rr2 = deepCopy(it1);
              rr2.w = it2.w;
              reTexts.push(rr2);
            } else {
              const rr2 = deepCopy(it1);
              rr2.w = it2.w;
              rr2.key = it2.exec[0];
              const kIdx = dict.get(it2.exec[0]);
              rr2.keyIdx0based = kIdx !== undefined ? kIdx : 0;
              reTexts.push(rr2);
            }
          }
        }
        return reTexts;
      }

      function texts2line(texts: DText[]) {
        const addresses = new VerseRange();
        addresses.verses = [a1 as DAddress];
        const rrr2: DOneLine = { children: texts, addresses };
        return rrr2;
      }
    }
    async function searchAsync(keyword: string): Promise<DSeApiRecord[]> {
      if (keyword === '摩西') {
        const rr1 = await searchAsyncTest1('摩西');
        console.log(rr1);
        return rr1;
      }
      // 實驗結果, api 一次有５００個限制, 就算你有加 count_only 或 index_only 仍然要加 limit 不然會是 null
      // 實驗結果, api 將 orig=1 不是將結果加上 sn 去查
      const records = await getAllIndexAsync(keyword);
      cvtChinesesToBookAndSecToVerse(records);

      await queryBibleTextViaQsbApiPost(records);
      return records;
    }


    async function queryBibleTextViaQsbApiPost(records: DSeApiRecord[]) {
      const refQsb = await getDataAsync();
      setBibleText(records, refQsb);
      return;

      async function getDataAsync() {
        const qstr = generateQstr();
        const body = `qstr=${qstr}`;
        const rr1 = await ajaxPost('https://bible.fhl.net/json/qsb.php', body)
          .pipe(map(a1 => a1.response as DSeApiResult)).toPromise();
        cvtChinesesToBookAndSecToVerse(rr1.record);
        return rr1.record;

        function generateQstr() {
          const addresses = new VerseRange();
          for (const it1 of records) {
            addresses.add(it1 as DAddress);
          }
          return addresses.toStringChineseShort();
        }
      }
      function setBibleText(data: DSeApiRecord[], refFromQsbApi: DSeApiRecord[]) {
        data.forEach(a1 => {
          const idx = refFromQsbApi
            .findIndex(a2 => isTheSameAddress(a1 as DAddress, a2 as DAddress));
          if (idx === -1) {
            console.warn(a1);
            console.warn('沒找到');
          } else {
            a1.bible_text = refFromQsbApi[idx].bible_text;
            refFromQsbApi.splice(idx, 1);
          }
        });
      }


    }
    // engs to book, sec to verse
    function cvtChinesesToBookAndSecToVerse(record: DSeApiRecord[]) {
      const dict = generateDictionay();
      for (const it1 of record) {
        it1.book = dict.get(it1.chineses);
        if (it1.book === undefined) {
          console.warn(it1);
        }
        it1.verse = it1.sec;
      }
      return;
      function generateDictionay() {
        // tslint:disable-next-line: max-line-length
        return LQ.from(BookNameConstants.CHINESE_BOOK_ABBREVIATIONS)
          .select((a1, i1) => {
            return { na: a1, idx: i1 + 1 };
          }).toDictionary(a1 => a1.na, a1 => a1.idx);
      }
    }

    async function getAllIndexAsync(keyword: string) {
      let re: DSeApiRecord[] = [];
      let offset = 0;
      while (true) {
        // tslint:disable-next-line: max-line-length
        const rr1 = (await ajax({ url: `https://bible.fhl.net/json/se.php?index_only=1&offset=${offset}&limit=500&orig=0&q=${keyword}` }).toPromise()).response as DSeApiResult;
        re = re.concat(rr1.record);
        offset += 500;
        if (rr1.record_count !== 500) {
          break;
        }
      }

      return re;

    }

    // orig:0 關鍵字 1:Greek 2:Hebrew
    // let re = ajax({ url: 'https://bible.fhl.net/json/se.php?q=18&orig=1&limit=500' });
    // let re = ajax({ url: 'https://bible.fhl.net/json/se.php?q=18&orig=1&limit=500' });
    let r1 = await ajax({ url: 'https://bible.fhl.net/json/se.php?q=愛裡&count_only=1' }).pipe(map(a1 => a1.response)).toPromise(); // record_count: 4
    r1 = await ajax({ url: 'https://bible.fhl.net/json/se.php?q=320&index_only=0&limit=500&orig=1' }).pipe(map(a1 => a1.response)).toPromise(); // record_count: 4
    // r1 = await ajax({ url: 'https://bible.fhl.net/json/se.php?q=摩西&count_only=1&index_only=0&' }).pipe(map(a1 => a1.response)).toPromise(); // record_count: 4
    console.log(r1);


    return [];
    let re1 = test01();
    let re2 = re1.map(a1 => this.cvt2DOneLineDText(a1));
    re2 = new AddBrStdandard().main(re2, re2[0].addresses);
    re2 = new AddSnInfo().main(re2, re2[0].addresses);
    return re2;
    /**
     * 一個 關鍵字 測試
     */
    function test01() {
      return [
        {
          bible_text: "這樣<WG3704>就可以作<WG1096><WTG5667>你們<WG4771>{<WG1722>}天<WG3772>{<WG3588>}父<WG3962>的兒子<WG5207>；因為<WG3754>他<WG846>叫日頭<WG2246>照<WG393><WTG5719>{<WG1909>}好人<WG18>，也<WG2532>照歹人<WG4190>；{<WG2532>}降雨<WG1026><WTG5719>給<WG1909>義人<WG1342>，也<WG2532>給不義的人<WG94>。",
          engs: "Matt",
          chap: 5,
          sec: 45,
        },
        {
          bible_text: "你們<WG4771>雖然<WG1487>{<WG1510>}{<WTG5723>}不好<WG4190>，尚且<WG3767>知道<WG3608a><WTG5758>拿好<WG18>東西<WG1390>給<WG1325><WTG5721>{<WG4771>}兒女<WG5043>，何況<WG4214>你們<WG4771>{<WG3588>}在<WG1722>天上<WG3772>的父<WG3962>，豈不更<WG3123>把好東西<WG18>給<WG1325><WTG5692>求<WG154><WTG5723>他<WG846>的人嗎？",
          engs: "Matt",
          chap: 7,
          sec: 11,
        },

      ];
    }

  }
  private cvt2DOneLineDText(a1: { bible_text: string; engs: string; chap: number; sec: number; }) {
    const addresses = new VerseRange();
    addresses.add({ book: new BookNameToId().cvtName2Id(a1.engs), chap: a1.chap, verse: a1.sec });
    const re3: DOneLine = {
      addresses,
      children: [
        {
          w: a1.bible_text
        }
      ],
    };
    return re3;
  }
}

